package com.ycz.pattern.factory.method;/** * 具体产品实现类 */public class ConcreateFactoryA extends AbstractFactory {    @Override    <T extends Product> T createProduct(Class<T> tClass) {        try {            return (T) Class.forName(tClass.getName()).newInstance();        } catch (Exception e) {           e.printStackTrace();        }        return null;    }    @Override    Product createProduct() {        return new ConcreateProductA();    }}